//apartdo donde se importan los comopoonetes necesarios de react
import React from 'react';
import { Menu, Icon, Badge } from 'antd';
import axios from 'axios';
import { USER_SERVER } from '../../../Config';
import { withRouter } from 'react-router-dom';
import { useSelector } from "react-redux";
//Inicio de menu de logeo y de registro
function MenuLogeo(props) {
  const user = useSelector(state => state.user)
  const logoutHandler = () => {
    axios.get(`${USER_SERVER}/logout`).then(response => {
      if (response.status === 200) {
        props.history.push("/login");
      } else {
        alert('Error para cerrar la secion')
      }
    });
  };
//validamos si el usurio no esta logeado que muestre el menu de logeo y registro
  if (user.userData && !user.userData.isAuth) {
    return (
      <Menu mode={props.mode}>
        <Menu.Item key="mail">
          <a href="/login">Registrarme</a>
        </Menu.Item>
        <Menu.Item key="app">
          <a href="/register">Tengo una cuenta</a>
        </Menu.Item>
      </Menu>
    )
  } else {
    return (
      <Menu mode={props.mode}>



        <Menu.Item key="upload">
          <a href="/product/upload">Nuevo articulo</a>
        </Menu.Item>

        <Menu.Item key="cart" style={{ paddingBottom: 3 }}>
          <Badge count={user.userData && user.userData.cart.length}>
            <a href="/user/cart" style={{ marginRight: -22 , color:'#667777'}}>
              <Icon type="shopping-cart" style={{ fontSize: 30, marginBottom: 3 }} />
            </a>
          </Badge>
        </Menu.Item>


        <Menu.Item key="logout">
          <a onClick={logoutHandler}>Cerrar Sesion</a>
        </Menu.Item>
      </Menu>
    )
  }
}

export default withRouter(MenuLogeo);
