import React, { useState } from 'react'
import { Typography, Button, Form, message, Input, Icon } from 'antd';
import FileUpload from '../../utils/subirimagen'
import Axios from 'axios';

const { Title } = Typography;
const { TextArea } = Input;

const Continents = [
    { key: 1, value: "Samsung" },
    { key: 2, value: "Dell" },
    { key: 3, value: "Toshiba" },
    { key: 4, value: "Mac" },
    { key: 5, value: "Compac" },
    { key: 6, value: "Sony" },
    { key: 7, value: "Php" }
]

function UploadProductPage(props) {

    const [TitleValue, setTitleValue] = useState("")
    const [DescriptionValue, setDescriptionValue] = useState("")
    const [PriceValue, setPriceValue] = useState(0)
    const [ContinentValue, setContinentValue] = useState(1)

    const [Images, setImages] = useState([])


    const onTitleChange = (event) => {
        setTitleValue(event.currentTarget.value)
    }

    const onDescriptionChange = (event) => {
        setDescriptionValue(event.currentTarget.value)
    }

    const onPriceChange = (event) => {
        setPriceValue(event.currentTarget.value)
    }

    const onContinentsSelectChange = (event) => {
        setContinentValue(event.currentTarget.value)
    }

    const updateImages = (newImages) => {
        setImages(newImages)
    }
    const onSubmit = (event) => {
        event.preventDefault();


        if (!TitleValue || !DescriptionValue || !PriceValue ||
            !ContinentValue || !Images) {
            return alert('llena todos los campos primero!')
        }

        const variables = {
            writer: props.user.userData._id,
            title: TitleValue,
            description: DescriptionValue,
            price: PriceValue,
            images: Images,
            continents: ContinentValue,
        }
//seccion de advertencias  si se guardo o no el articulo deseado

//seccion del formulario para subir un nuevo articulo
    return (
        <div style={{ maxWidth: '700px', margin: '2rem auto' }}>
            <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
                <Title level={2}> Subir un Articulo</Title>
            </div>


            <Form onSubmit={onSubmit} >

                {/* aca es donde elegimos la imgaen del articulo */}
                <FileUpload refreshFunction={updateImages} />

                <br />
                <br />
                <label>Nombre del Producto: </label>
                <Input
                    onChange={onTitleChange}
                    value={TitleValue}
                />
                <br />
                <br />
                <label>Descripcion del Producto: </label>
                <TextArea
                    onChange={onDescriptionChange}
                    value={DescriptionValue}
                />
                <br />
                <br />
                <label>Precio Q: </label>
                <Input
                    onChange={onPriceChange}
                    value={PriceValue}
                    type="number"
                />
                <br /><br />
                <label>Seleccione una Categoria:    </label>
                <select onChange={onContinentsSelectChange} value={ContinentValue}>
                    {Continents.map(item => (
                        <option key={item.key} value={item.key}>{item.value} </option>
                    ))}
                </select>
                <br />
                <br />
                <Button onClick={onSubmit} >
                    Aceptar
                </Button>
            </Form>
        </div>
    )
}
Axios.post('/api/product/uploadProduct', variables)
.then(response => {
    if (response.data.success) {
        alert('Se guardo el articulo satisfactorimente')
        props.history.push('/')
    } else {
        alert('Erron al guardat este articulo')
    }
})

}
export default UploadProductPage
